package com.eval.year2025.educative._2SlidingWindow.RepeatedDNASequences;


import org.junit.jupiter.api.Test;

import java.util.List;

import static org.assertj.core.api.Java6Assertions.assertThat;

class SolutionTest {

    private final Solution solution = new Solution();


    @Test
    void example1(){
        String s = "AAAAACCCCCAAAAACCCCCCAAAAAGGGTTT";

        List<String> repeatedDnaSequences = solution.findRepeatedDnaSequences(s);

        assertThat(repeatedDnaSequences).contains("AAAAACCCCC", "CCCCCAAAAA");
    }


    @Test
    void example2(){
        String s = "AAAAACCCCCAAAAACCCCCCAAAAAGGGTTT";

        List<String> repeatedDnaSequences = solution.findRepeatedDnaSequencesPassButNotThatEfficient(s);

        assertThat(repeatedDnaSequences).contains("AAAAACCCCC", "CCCCCAAAAA");
    }


    @Test
    void example3(){
        String s = "";

        List<String> repeatedDnaSequences = solution.findRepeatedDnaSequences(s);

        assertThat(repeatedDnaSequences).contains("ATGCCAACAT","TGCCAACATT","GCCAACATTC","GCGTGAGGTG","CGTGAGGTGA","GGCCAGGCGA","AGAGGGAGCC","GAGGGAGCCG","CGGGTTACGC","CGCAGAGTTA","TCCTAAGCAC","CCTAAGCACA","CTAAGCACAA","TCGATGGTAT","CGATGGTATC","TCACTCCGCG","GGCAGTTGGC","GCAGTTGGCT","CGCGCCACCC","CAATTTGCCT","ATCGGGGTGG","CTGAACACAG","CTTTAGCGAG","TTTAGCGAGT");
    }

}